class ApiError extends Error {
  constructor (message, type, params) {
    super(message)
    this.name = 'RecurlyApiError'
    this.type = type
    this.params = params || []
  }
}

module.exports = ApiError

'use strict'

const https = require('https')

const pkg = require('../../package')

const ApiError = require('./ApiError')

const resourceCaster = require('./Caster')

// TODO terrible name
class BaseClient {
  constructor (apiKey, siteId) {
    this.siteId = siteId
    this.httpOptions = {
      // TODO will need to be configurable from playground
      // at some point
      host: 'partner-api.recurly.com',
      headers: {
        'Accept': `application/vnd.recurly.${this.apiVersion()}`,
        // TODO need a standardized user agent
        'User-Agent': `Recurly/${pkg.name}:${pkg.version}`,
        // TODO probably a standard way to do this with request
        'Authorization': 'Basic ' + Buffer.from(apiKey + ':', 'ascii').toString('base64')
      }
    }
  }

  _interpolatePath (path, parameters = {}) {
    parameters['site_id'] = this.siteId
    Object.keys(parameters).forEach(name => {
      path = path.replace(`{${name}}`, parameters[name])
    })
    return path
  }

  _makeRequest (method, path, request) {
    const options = Object.assign({
      method: method,
      path: path
    }, this.httpOptions)

    let requestBody
    if (request) {
      requestBody = JSON.stringify(request)
      options.headers['Content-Type'] = 'application/json'
      options.headers['Content-Length'] = Buffer.byteLength(requestBody)
    }

    return new Promise((resolve, reject) => {
      options.agent = new https.Agent(options)
      console.log('Sending Request: ', options)
      const request = https.request(options, (response) => {
        const body = []
        response.setEncoding('utf8')
        response.on('data', (chunk) => body.push(chunk))
        response.on('end', () => {
          let jsonBody = JSON.parse(body.join(''))
          console.log('Got Response: ', jsonBody)

          if (response.statusCode < 200 || response.statusCode > 299) {
            const err = jsonBody.error
            reject(new ApiError(err.message, err.type, err.params))
          } else {
            resolve(resourceCaster(jsonBody))
          }
        })
      })
      request.on('error', err => {
        reject(err)
      })
      if (requestBody) {
        request.write(requestBody)
      }
      request.end()
    })
  }
}

module.exports = BaseClient

const resources = require('./resources')

const dtRegex = /^(-?(?:[1-9][0-9]*)?[0-9]{4})-(1[0-2]|0[1-9])-(3[01]|0[1-9]|[12][0-9])T(2[0-3]|[01][0-9]):([0-5][0-9]):([0-5][0-9])(\\.[0-9]+)?(Z)?$/

function className (obj) {
  const objName = obj.object
  if (!objName) return null
  return objName
    .replace(/_([a-z])/g, g => g[1].toUpperCase())
    .replace(/^\w/, g => g.toUpperCase())
}

function construct (Klass, data) {
  return Object.assign(new Klass(), data)
}

function cast (obj) {
  const name = className(obj)
  const Klass = resources[name]
  // if it doesn't have a class name,
  // just return as a hash for now
  // might want to cast these values though
  if (!(name && Klass)) return obj

  // I assume we don't need this, maybe we
  // want to preserve it for some reason
  delete obj['object']

  const resource = construct(Klass, obj)
  for (let key in resource) {
    const value = resource[key]

    if (value) {
      // if it's a resource, cast the item
      if (value.object) {
        resource[key] = cast(value)
      // if it's an array, cast each element
      } else if (Array.isArray(value)) {
        if (value.length > 0 && value[0].object) {
          resource[key] = value.map(v => cast(v))
        }
      // cast other special types like datetimes
      } else if (typeof value === 'string') {
        // TODO check the performance of this approach
        if (value.match(dtRegex)) {
          resource[key] = new Date(value)
        }
      }
    }
  }

  return resource
}

module.exports = cast

'use strict'

let fs = require('fs')

let path = require('path')

let resourcesPath = path.join(__dirname, 'resources')
module.exports = fs.readdirSync(resourcesPath).reduce((acc, file) => {
  let className = file.split('/').reverse()[0].split('.')[0]
  acc[className] = require(`./resources/${file}`)
  return acc
}, {})

'use strict'

let resources = require('./recurly/resources')
module.exports = Object.assign(resources, {
  'Client': require('./recurly/Client')
})

